import { useState } from "react"
import React from "react";

function Tag() {

    const [tags, setTags] = useState([])

    const pushTextToTags = inputText => {
        let newTagElement;

        const tagInput = React.createRef();
        if(inputText[inputText.length - 1]===','){
            newTagElement = inputText.slice(0,inputText.length-1)
            setTags([ ...tags, newTagElement]);
        };
    }
  return (
    <div>
    <div>Tags</div>
    <div className='tagArea'></div>
    <div className='tagContainer'> 
    {tags.map(tag => (<div>{tag}</div>))}
    </div>
    <input type='text' onChange={e=>pushTextToTags(e.target.value)}></input>
    </div>
  )
}

export default Tag
