import { useState } from "react"
import React from "react";
import './Tag.css';
import TagItem from "./TagItem";

function Tag() {

    const [tags, setTags] = useState([])
    
    const tagInput = React.createRef();

    const cullTexttoTags = textToRemove => {
      tags.filter(tag => tag !== textToRemove)
    }

    const pushTextToTags = inputText => {
        let newTagElement;


        if(inputText[inputText.length - 1]===','){
            newTagElement = inputText.slice(0,inputText.length-1);
            tagInput.current.value = '';
            setTags([ ...tags, newTagElement]);
        };
    }
  return (
    <div className="tag">
      <div className="tagArea">
        <div className="tagContainer"> 
          {tags.map(tag => (<TagItem text={tag}/>))}
        </div>
      
      <input ref={tagInput} type='text' onChange={e=>pushTextToTags(e.target.value)}></input>
    </div>
    </div>
  )
}

export default Tag
